-- Create "seats" table
CREATE TABLE `seats` (`id` varchar(16) NOT NULL COMMENT "Unique identifier for the seat", `row` varchar(255) NULL COMMENT "Row identifier for the seat", `label` varchar(255) NULL COMMENT "Label for the seat", `type` varchar(255) NULL COMMENT "Type of the seat (e.g., VIP, Regular)", `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT "Timestamp when the seat was created", `updated_at` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT "Timestamp when the seat was last updated", `deleted_at` datetime NULL COMMENT "Timestamp when the seat was deleted", `created_by` varchar(16) NULL COMMENT "Identifier of the user who created the seat", `updated_by` varchar(16) NULL COMMENT "Identifier of the user who last updated the seat", `deleted_by` varchar(16) NULL COMMENT "Identifier of the user who deleted the seat", PRIMARY KEY (`id`)) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT "Table for storing seat information";
-- Create "event_seats" table
CREATE TABLE `event_seats` (`id` varchar(16) NOT NULL COMMENT "Unique identifier for the event seat", `event_id` varchar(16) NULL COMMENT "Identifier of the event associated with the seat", `name` varchar(255) NULL COMMENT "Name of the event seat", `seat_map_data` text NULL COMMENT "Seat map data for the event", `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT "Timestamp when the event seat was created", `updated_at` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT "Timestamp when the event seat was last updated", `deleted_at` datetime NULL COMMENT "Timestamp when the event seat was deleted", `created_by` varchar(16) NULL COMMENT "Identifier of the user who created the event seat", `updated_by` varchar(16) NULL COMMENT "Identifier of the user who last updated the event seat", `deleted_by` varchar(16) NULL COMMENT "Identifier of the user who deleted the event seat", PRIMARY KEY (`id`), INDEX `fk_event_seats_events` (`event_id`), CONSTRAINT `fk_event_seats_events` FOREIGN KEY (`event_id`) REFERENCES `events` (`id`) ON UPDATE CASCADE ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT "Table for storing event seat information";
