-- Create "order_ticket_images" table
CREATE TABLE `order_ticket_images` (`id` varchar(16) NOT NULL COMMENT "Primary key for the order_ticket_images table", `order_id` varchar(16) NULL COMMENT "Foreign key referencing the order associated with the ticket image", `code` varchar(16) NULL COMMENT "Unique code for the ticket image", `ticket_name` varchar(255) NULL COMMENT "Name of the ticket", `seat_location` varchar(255) NULL COMMENT "Location of the seat associated with the ticket", `qr_ticket_url` varchar(255) NULL COMMENT "URL of the QR code for the ticket", `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT "Timestamp when the ticket image was created", `updated_at` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT "Timestamp when the ticket image was last updated", `created_by` varchar(16) NULL COMMENT "Identifier of the user who created the ticket image", `updated_by` varchar(16) NULL COMMENT "Identifier of the user who last updated the ticket image", PRIMARY KEY (`id`), INDEX `fk_order_ticket_images_orders` (`order_id`), CONSTRAINT `fk_order_ticket_images_orders` FOREIGN KEY (`order_id`) REFERENCES `orders` (`id`) ON UPDATE CASCADE ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT "Table for storing ticket images associated with orders";
